function addItems($items): void
{
  foreach ($items as $item) {
    $db_item = new Item;
    $db_item->name = $item['name'];
    $db_item->url = $item['url'];
    $db_item->cost = $item['cost'];
    $db_item->rarity = $item['rarity'];
    $db_item->type = $item['type'];
    $db_item->save();
  }

}
